INSERT INTO [dbo].[tblRecipeInstruction] ([Id], [Recipe Id], [InstructionNum], [Instruction], [ImagePath]) VALUES (N'585d2882-8ba2-40bc-af48-0217b572cbba', N'c029fad3-09a0-43ba-8a74-4167b1d975e3', 4, N'Baste with butter and herbs (optional) - If you want more flavor, add the butter and herbs to the pan during the last couple minutes of cooking and baste it with the butter.', N'ImagePath')
INSERT INTO [dbo].[tblRecipeInstruction] ([Id], [Recipe Id], [InstructionNum], [Instruction], [ImagePath]) VALUES (N'eebda0b8-28fc-49a5-804a-09b4996f0b50', N'c029fad3-09a0-43ba-8a74-4167b1d975e3', 2, N'Heat the skillet - Heat a cast-iron or stainless-steel skillet large enough to hold the steak on the stove over medium-high heat until very hot and it just barely starts to smoke, about 3 minutes', N'ImagePath')
INSERT INTO [dbo].[tblRecipeInstruction] ([Id], [Recipe Id], [InstructionNum], [Instruction], [ImagePath]) VALUES (N'c0be370a-e142-4031-895c-2426f284d822', N'f30fa144-f97a-4899-aa8a-6ea2226347db', 4, N'Stir in white beans; continue cooking and stirring until pasta is tender, 4-5 minutes. Remove from heat and stir in grated cheese. Served topped with additional grated cheese.', N'ImagePath')
INSERT INTO [dbo].[tblRecipeInstruction] ([Id], [Recipe Id], [InstructionNum], [Instruction], [ImagePath]) VALUES (N'09a771eb-5230-4e7e-8efe-2e93a1f06010', N'7c22917d-bb42-48fc-83ca-d2fb20ffa0f4', 1, N'Preheat oven to 400°. In a small saucepan, prepare rice according to package instructions. In a large skillet over medium heat, heat oil. Cook onion until soft, about 5 minutes. Stir in tomato paste and garlic and cook until fragrant, about 1 minute more. Add ground beef and cook, breaking up meat with a wooden spoon, until no longer pink, 6 minutes. Drain fat.', N'ImagePath')
INSERT INTO [dbo].[tblRecipeInstruction] ([Id], [Recipe Id], [InstructionNum], [Instruction], [ImagePath]) VALUES (N'de997294-5ff8-415d-ab7f-3b7e9f43448b', N'1aeade99-fb83-453a-9387-4f7fb19c981d', 2, N'Season the Shrimp - In a large bowl, combine the shrimp, blackening seasoning, and salt', N'ImagePath')
INSERT INTO [dbo].[tblRecipeInstruction] ([Id], [Recipe Id], [InstructionNum], [Instruction], [ImagePath]) VALUES (N'25926466-29b5-46a9-8a46-552c77b2bba3', N'f30fa144-f97a-4899-aa8a-6ea2226347db', 2, N'Stir in tomato paste until evenly distributed, 2-3 minutes. Pour in 3 cups broth; increase heat to high and bring to a boil. Stir in red pepper flakes, oregano, salt, and pepper. Reduce heat to medium and let simmer, stirring often, for about 5 minutes. Add more broth if needed.', N'ImagePath')
INSERT INTO [dbo].[tblRecipeInstruction] ([Id], [Recipe Id], [InstructionNum], [Instruction], [ImagePath]) VALUES (N'b591f8ed-a01d-4db6-90a2-608154b00980', N'c029fad3-09a0-43ba-8a74-4167b1d975e3', 1, N'Pat the steak dry - Pat the steak dry with paper towels and season it liberally with salt all over, let rest at room temperature for 30 minutes', N'ImagePath')
INSERT INTO [dbo].[tblRecipeInstruction] ([Id], [Recipe Id], [InstructionNum], [Instruction], [ImagePath]) VALUES (N'5c44833a-391b-4003-a03b-6683486d40f6', N'1aeade99-fb83-453a-9387-4f7fb19c981d', 7, N'Garnish and serve - garnish and serve with cheese and cilantro', N'ImagePath')
INSERT INTO [dbo].[tblRecipeInstruction] ([Id], [Recipe Id], [InstructionNum], [Instruction], [ImagePath]) VALUES (N'27e3eab2-7177-455a-a821-6ce9be79b3f1', N'f30fa144-f97a-4899-aa8a-6ea2226347db', 3, N'Place chopped chard in a bowl. Cover with cold water and rinse leaves; any grit will fall to the bottom of the bowl. Transfer chard to a colander to drain briefly; add to soup. Cook and stir until leaves wilt, 2-3 minutes.', N'ImagePath')
INSERT INTO [dbo].[tblRecipeInstruction] ([Id], [Recipe Id], [InstructionNum], [Instruction], [ImagePath]) VALUES (N'bce9bea7-09f9-4095-8427-938ee8ad5387', N'7c22917d-bb42-48fc-83ca-d2fb20ffa0f4', 5, N'Garnish with parsley before serving.', N'Image Path')
INSERT INTO [dbo].[tblRecipeInstruction] ([Id], [Recipe Id], [InstructionNum], [Instruction], [ImagePath]) VALUES (N'589d55bf-da0f-48b1-a277-967f1daa2cee', N'c029fad3-09a0-43ba-8a74-4167b1d975e3', 3, N'Pat the steak dry (again) and cook - pat the steak dry again with paper towels, add the steak, and cook, pressing down to make sure it gets good contact with the pan, until a nice sear starts to form, about 2 minutes; flip over and cook for 2 minutes more. Then if you want a more well done steak, reduce the heat to medium low and continue to cook, flipping over repeatedly each minute, until the steak is as cooked as you desire', N'ImagePath')
INSERT INTO [dbo].[tblRecipeInstruction] ([Id], [Recipe Id], [InstructionNum], [Instruction], [ImagePath]) VALUES (N'259df69b-f4dc-4535-9e8d-9b92cb08b383', N'c029fad3-09a0-43ba-8a74-4167b1d975e3', 5, N'Serve - Remove the steak from the pan and let rest for a couple of minutes before slicing. If desired, sprinkle with some sea salt. Serve.', N'ImagePath')
INSERT INTO [dbo].[tblRecipeInstruction] ([Id], [Recipe Id], [InstructionNum], [Instruction], [ImagePath]) VALUES (N'66543f73-1580-4d83-b370-9dcb7eeb2a53', N'1aeade99-fb83-453a-9387-4f7fb19c981d', 1, N'Preheat the oven to 300 degrees F - Wrap the tortillas in foil and place them on a baking sheet or pan', N'ImagePath')
INSERT INTO [dbo].[tblRecipeInstruction] ([Id], [Recipe Id], [InstructionNum], [Instruction], [ImagePath]) VALUES (N'd750c57a-7f20-4924-9f5f-a31dcc6debfc', N'1aeade99-fb83-453a-9387-4f7fb19c981d', 4, N'Make the cabbage slaw - In a large bowl, combine the cabbage, mayo, sugar, black pepper, and white vinegar', N'ImagePath')
INSERT INTO [dbo].[tblRecipeInstruction] ([Id], [Recipe Id], [InstructionNum], [Instruction], [ImagePath]) VALUES (N'dfe22f95-6ac6-41a8-bc25-aab0345c5186', N'7c22917d-bb42-48fc-83ca-d2fb20ffa0f4', 3, N'Place peppers cut side-up in a 9"-x-13" baking dish and drizzle with oil. Spoon beef mixture into each pepper and top with Monterey jack, then cover baking dish with foil.', N'ImagePath')
INSERT INTO [dbo].[tblRecipeInstruction] ([Id], [Recipe Id], [InstructionNum], [Instruction], [ImagePath]) VALUES (N'01cacca6-b897-4bdf-be79-ad3e6b7c0de4', N'1aeade99-fb83-453a-9387-4f7fb19c981d', 6, N'Assemble the tortillas - Place the tortillas in a serving platter or plates. Divide the shrimp and slaw equally between the tortillas', N'ImagePath')
INSERT INTO [dbo].[tblRecipeInstruction] ([Id], [Recipe Id], [InstructionNum], [Instruction], [ImagePath]) VALUES (N'36f37e20-e8da-4e59-8830-b13098f30b4b', N'7c22917d-bb42-48fc-83ca-d2fb20ffa0f4', 2, N'Return beef mixture to skillet, then stir in cooked rice and diced tomatoes. Season with oregano, salt, and pepper. Let simmer until liquid has reduced slightly, about 5 minutes.', N'ImagePath')
INSERT INTO [dbo].[tblRecipeInstruction] ([Id], [Recipe Id], [InstructionNum], [Instruction], [ImagePath]) VALUES (N'82910ae8-8c9b-4f06-8219-b208321ed7f3', N'1aeade99-fb83-453a-9387-4f7fb19c981d', 5, N'Cook the shrimp - In a large nonstick skillet over medium-high heat, add in the oil. Once it’s hot, add the shrimp. Cook until the shrimp have slightly browned on the outside and they’ve cooked through turning pink throughout, flipping halfway through, 2-3 minutes per side', N'ImagePath')
INSERT INTO [dbo].[tblRecipeInstruction] ([Id], [Recipe Id], [InstructionNum], [Instruction], [ImagePath]) VALUES (N'77d29c46-a562-4af8-8325-b28f98cf9894', N'7c22917d-bb42-48fc-83ca-d2fb20ffa0f4', 4, N'Bake until peppers are tender, about 35 minutes. Uncover and bake until cheese is bubbly, 10 minutes more.', N'Image Path')
INSERT INTO [dbo].[tblRecipeInstruction] ([Id], [Recipe Id], [InstructionNum], [Instruction], [ImagePath]) VALUES (N'6041d1b2-c8e4-4335-a598-dd3366d484a0', N'1aeade99-fb83-453a-9387-4f7fb19c981d', 8, N'Storage - Try to eat all assembled tacos that same day. If you have leftover shrimp or slaw, store in separate air-tight containers in the fridge for up to 2 days', N'ImagePath')
INSERT INTO [dbo].[tblRecipeInstruction] ([Id], [Recipe Id], [InstructionNum], [Instruction], [ImagePath]) VALUES (N'4a8e44d1-9ae4-4299-a705-ed1a9a228d4f', N'1aeade99-fb83-453a-9387-4f7fb19c981d', 3, N'Warm the tortillas - Bake the foil-wrapped tortillas until they’re warmed through, about 10 minutes (if your tortillas are done before you finish the slaw and shrimp, turn off the oven, and leave the tortillas inside to stay warm until you are ready to assemble', N'ImagePath')
INSERT INTO [dbo].[tblRecipeInstruction] ([Id], [Recipe Id], [InstructionNum], [Instruction], [ImagePath]) VALUES (N'322c6e2f-8442-4539-a1e4-f6c26b888391', N'f30fa144-f97a-4899-aa8a-6ea2226347db', 1, N'Heat oil in a skillet over medium-high heat. Cook and stir sausage in the hot skillet until browned and crumbly, about 5 minutes. Reduce heat to medium. Add diced celery and chopped onion. Cook until onions are translucent, 4-5 minutes. Add dry pasta; cook and stir for 2 minutes', N'ImagePath')
